    def showTime(self):
        currentTime = QTime.currentTime()
        displaytime = currentTime.toString('hh:mm:ss')
        self.current_time.setText(displaytime)
        timer = QTimer()
        timer.timeout.connect(self.showTime)
        timer.start(1000) # update every second
       
        

